from todo_api.domain.entity.entity import Task
from todo_api.memdb.memdb import MemDB


class OperationInteractor:
    def __init__(self, memdb: MemDB):
        self._db = memdb

    def show_tasks(self) -> list[Task]:
        return self._db.search_unfinished()

    def create_task(self, task: Task) -> Task:
        task["done"] = False
        self._db.add(task)
        return task


    def done_task(self, task_id: int) -> Task:
        task = self._db.get(task_id)
        if task is None:
            raise Exception("not found")
        task["done"] = True
        self._db.update(task)
        return task

    def update_task(self, task_id: int, text: str) -> Task:
        task = self._db.get(task_id)
        if task is None:
            raise Exception("not found")
        task["text"] = text
        self._db.update(task)
        return task

import { Task } from "../entity/task";

export async function loadTasks(): Promise<Task[]> {
    const url = "/api/tasks";
    const res = await fetch(url, { method: "GET" });
    return await res.json();
}

export async function postTask(task: Task): Promise<Task[]> {
    const url = "/api/tasks";
    const res = await fetch(url, {
        method: "POST",
        body: JSON.stringify(task),
        headers: {
            "Content-Type": "application/json",
        },
    });
    return await res.json();
}

export async function postTaskDone(task: Task): Promise<void> {
    const url = `/api/tasks/${task.id}/done`;
    await fetch(url, {
        method: "PATCH",
        headers: {
            "Content-Type": "application/json",
        },
    });
}


export async function patchTask(task: Task): Promise<void> {
    const url = `/api/tasks/${task.id}`;
    await fetch(url, {
        method: "PATCH",
        body: JSON.stringify({ text: task.text }),
        headers: {
            "Content-Type": "application/json",
        },
    });
}

