import { Task } from "../entity/task";
import * as api from "../api/task";

interface ListTaskViewProps {
    taskList: Task[];
    reloadTasks: () => Promise<void>;
}

export const ListTaskView = (props: ListTaskViewProps) => {
    const cards = props.taskList.map((task) => (
        <TaskCard
            task={task}
            reloadTasks={props.reloadTasks}
            key={task.id}
        ></TaskCard>
    ));
    return <div className="row p-2">{cards}</div>;
};

interface TaskCardProps {
    task: Task;
    reloadTasks: () => Promise<void>;
}

import React, { useState } from "react";

const TaskCard = (props: TaskCardProps) => {
    const [isEditing, setIsEditing] = useState(false);
    const [editText, setEditText] = useState(props.task.text);

    async function clickDone(): Promise<void> {
        await api.postTaskDone(props.task);
        props.reloadTasks();
    }

    async function saveEdit() {
        await api.patchTask({ ...props.task, text: editText });
        setIsEditing(false);
        props.reloadTasks();
    }

    function cancelEdit() {
        setEditText(props.task.text);
        setIsEditing(false);
    }

    return (
        <div className="card m-2" style={{ width: "28rem" }}>
            <div className="card-body">
                <h5 className="card-title">{props.task.id}</h5>
                {isEditing ? (
                    <>
                        <input
                            type="text"
                            className="form-control mb-2"
                            value={editText}
                            onChange={e => setEditText(e.target.value)}
                        />
                        <button className="btn btn-success me-2" onClick={saveEdit}>
                            保存
                        </button>
                        <button className="btn btn-secondary" onClick={cancelEdit}>
                            キャンセル
                        </button>
                    </>
                ) : (
                    <>
                        <p className="card-text">{props.task.text}</p>
                        <button className="btn btn-outline-primary me-2" onClick={() => setIsEditing(true)}>
                            編集
                        </button>
                        <button className="btn btn-primary" onClick={clickDone}>
                            done
                        </button>
                    </>
                )}
            </div>
        </div>
    );
};

